import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './MpesaPaymentForm.css';

const MpesaPaymentForm = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { event } = location.state || {};

  const [ticketQuantity, setTicketQuantity] = useState(1);
  const [totalPrice, setTotalPrice] = useState(event ? event.event_price : 0);
  const [phoneNumber, setPhoneNumber] = useState('');
  const [pin, setPin] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    if (event) {
      setTotalPrice(event.event_price * ticketQuantity);
    }
  }, [ticketQuantity, event]);

  const handleQuantityChange = (increment) => {
    setTicketQuantity((prevQuantity) => {
      if (prevQuantity + increment >= 1) {
        return prevQuantity + increment;
      }
      return 1; 
    });
  };

  const validatePhoneNumber = (phoneNumber) => {
    const phonePattern = /^(84|85)\d{7}$/;
    return phonePattern.test(phoneNumber);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage('');

    if (!validatePhoneNumber(phoneNumber)) {
      setErrorMessage('Please enter a valid M-Pesa phone number starting with 84 or 85 and containing 9 digits.');
      return;
    }

    if (!pin) {
      setErrorMessage('Please provide a valid pin.');
      return;
    }

    try {
      const token = localStorage.getItem('jwt_token'); // Get the JWT token from local storage
      const response = await fetch('/simulate-mpesa-payment', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({
          phone_number: phoneNumber,
          amount: totalPrice,
          event_id: event.event_id,
          tickets_purchased: ticketQuantity,
        }),
      });

      const data = await response.json();
      if (data.status === 'Success') {
        alert('Payment successful!');
        navigate('/landing');
      } else {
        setErrorMessage('Payment failed. Please try again.');
      }
    } catch (error) {
      setErrorMessage('An error occurred during payment. Please try again.');
    }
  };

  return (
    <div className="mpesa-payment-container">
      <h2>Pay with M-Pesa for {event?.event_name}</h2>
      <form onSubmit={handleSubmit} className="mpesa-payment-form">
        <div className="form-group">
          <label>Price (per ticket):</label>
          <input type="text" value={`$${event?.event_price}`} disabled />
        </div>

        <div className="form-group">
          <label>Quantity:</label>
          <div className="quantity-input">
            <button type="button" onClick={() => handleQuantityChange(-1)}>&minus;</button>
            <input type="number" value={ticketQuantity} readOnly />
            <button type="button" onClick={() => handleQuantityChange(1)}>&#43;</button>
          </div>
        </div>

        <div className="form-group">
          <label>Total Price:</label>
          <input type="text" value={`$${totalPrice.toFixed(2)}`} disabled />
        </div>

        <div className="form-group">
          <label>Phone Number:</label>
          <input
            type="tel"
            value={phoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
            required
            placeholder="Enter phone number (e.g., 841234567)"
          />
        </div>

        <div className="form-group">
          <label>Pin:</label>
          <input
            type="password"
            value={pin}
            onChange={(e) => setPin(e.target.value)}
            required
          />
        </div>

        {errorMessage && <div className="error-message">{errorMessage}</div>}

        <button type="submit">Pay Now</button>
      </form>
    </div>
  );
};

export default MpesaPaymentForm;
