import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './HostDashboardPage.css';

const HostDashboardPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const username = location.state?.username || "Host";

  const [profileStatus, setProfileStatus] = useState(null);

  useEffect(() => {
    const fetchProfileStatus = async () => {
      try {
        const response = await fetch('/api/profile-status');
        if (response.ok) {
          const data = await response.json();
          setProfileStatus(data.status);
        } else {
          console.error('Failed to fetch profile status');
        }
      } catch (error) {
        console.error('Error fetching profile status:', error);
      }
    };

    fetchProfileStatus();
  }, []);

  const handleCreateEvent = () => {
    navigate('/create-event');
  };

  const handleGetStarted = () => {
    navigate('/vetting');
  };

  const handleViewEvents = () => {
    navigate('/host-events');
  };

  const handleLogout = () => {
    navigate('/login');
  };

  return (
    <div className="host-dashboard">
      <header className="header">
        <nav className="nav-links">
          <ul>
            <li><button onClick={handleLogout}>Logout</button></li>
            <li>
              <div className="settings-dropdown">
                <button>Profile</button>
                <div className="dropdown-content">
                  <button onClick={() => navigate('/change-password')}>Change Password</button>
                  <button onClick={() => navigate('/host/payment-account')}>Payment Account</button>
                  <button onClick={() => navigate('/host/reports')}>Reports</button>
                </div>
              </div>
            </li>
          </ul>
        </nav>
      </header>

      <div className="content">
        <h1>Welcome, {username}!</h1>
        {profileStatus === 'Pending' && (
          <p>Your profile is in vetting process, await approval to continue.</p>
        )}
        <button
          className="get-started-button"
          onClick={handleGetStarted}
          disabled={profileStatus !== null}
          style={profileStatus !== null ? { backgroundColor: '#ccc', cursor: 'not-allowed' } : {}}
        >
          Get Started
        </button>
        <button
          className="create-event-button"
          onClick={handleCreateEvent}
          disabled={profileStatus !== 'Approved'}
          style={profileStatus !== 'Approved' ? { backgroundColor: '#ccc', cursor: 'not-allowed' } : {}}
        >
          <span>+</span> Publish Event
        </button>
        <button
          className="view-events-button"
          onClick={handleViewEvents}
          disabled={profileStatus !== 'Approved'}
          style={profileStatus !== 'Approved' ? { backgroundColor: '#ccc', cursor: 'not-allowed' } : {}}
        >
          View My Events
        </button>
      </div>
    </div>
  );
};

export default HostDashboardPage;
