import React, { useState, useEffect } from 'react';
import { useStripe, useElements, CardNumberElement, CardExpiryElement, CardCvcElement } from '@stripe/react-stripe-js';
import './CheckoutForm.css';

const CheckoutForm = ({ event }) => {
  const stripe = useStripe();
  const elements = useElements();
  const [clientSecret, setClientSecret] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState(null);
  const [ticketQuantity, setTicketQuantity] = useState(1); // Default quantity is 1
  const [totalPrice, setTotalPrice] = useState(event.event_price); // Total price based on quantity

  useEffect(() => {
    // Update the total price whenever ticket quantity changes
    setTotalPrice(event.event_price * ticketQuantity);
  }, [ticketQuantity, event.event_price]);

  useEffect(() => {
    // Create a PaymentIntent when the component mounts or total price changes
    const createPaymentIntent = async () => {
      try {
        const response = await fetch('/create-payment-intent', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ amount: totalPrice * 100 }), // Stripe expects amount in cents
        });
        const { clientSecret } = await response.json();
        setClientSecret(clientSecret);
      } catch (error) {
        console.error('Error creating payment intent:', error);
      }
    };

    createPaymentIntent();
  }, [totalPrice]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setErrorMessage(null);

    if (!stripe || !elements) {
      setErrorMessage('Stripe is not loaded properly.');
      setIsLoading(false);
      return;
    }

    const cardNumberElement = elements.getElement(CardNumberElement);

    // Confirm the payment using the clientSecret
    const { paymentIntent, error } = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: cardNumberElement,
      },
    });

    if (error) {
      console.error('Payment failed:', error);
      setErrorMessage(error.message);
      setIsLoading(false);
    } else if (paymentIntent && paymentIntent.status === 'succeeded') {
      // Call the backend to finalize the payment and record the transaction
      try {
        const response = await fetch('/record-transaction', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            payment_status: 'Success',
            event_id: event.event_id,
            amount_paid: totalPrice,
            tickets_purchased: ticketQuantity,
          }),
        });

        if (response.ok) {
          alert('Payment successful!');
          // Optionally navigate to another page or refresh the event data
        } else {
          const data = await response.json();
          setErrorMessage(data.error || 'Failed to record payment');
        }
      } catch (err) {
        setErrorMessage(`Failed to complete payment: ${err.message}`);
      } finally {
        setIsLoading(false);
      }
    }
  };

  const handleQuantityChange = (increment) => {
    setTicketQuantity((prevQuantity) => {
      if (prevQuantity + increment >= 1) {
        return prevQuantity + increment;
      }
      return 1; // Minimum quantity of 1
    });
  };

  return (
    <div className="checkout-form-container">
      <img src={`/event_images/${event.event_poster}`} alt={event.event_name} className="event-image" />
      <h3>{event.event_name}</h3>

      <form onSubmit={handleSubmit} className="checkout-form">
        {/* Price and Quantity Section */}
        <div className="price-tickets-container">
          <div className="form-group">
            <label htmlFor="price">Price (per ticket):</label>
            <input
              type="text"
              className="price-input"
              value={`$${event.event_price}`}
              disabled
            />
          </div>

          <div className="form-group">
            <label htmlFor="tickets">Quantity:</label>
            <div className="quantity-input">
              <button
                type="button"
                onClick={() => handleQuantityChange(-1)}
                className="quantity-arrow"
              >
                &minus;
              </button>
              <input
                type="number"
                className="tickets-input"
                value={ticketQuantity}
                readOnly
              />
              <button
                type="button"
                onClick={() => handleQuantityChange(1)}
                className="quantity-arrow"
              >
                &#43;
              </button>
            </div>
          </div>
        </div>

        <div className="form-group">
          <label htmlFor="total-price">Total Price:</label>
          <input
            type="text"
            className="price-input"
            value={`$${totalPrice.toFixed(2)}`} // Dynamically updating total price
            disabled
          />
        </div>

        {/* Individual Stripe Elements */}
        <div className="form-group">
          <label htmlFor="cardNumber">Card Number:</label>
          <CardNumberElement className="card-input" />
        </div>

        <div className="form-group">
          <label htmlFor="cardExpiry">Expiration Date:</label>
          <CardExpiryElement className="card-input" />
        </div>

        <div className="form-group">
          <label htmlFor="cardCvc">CVC:</label>
          <CardCvcElement className="card-input" />
        </div>

        {errorMessage && <div className="error-message">{errorMessage}</div>}

        {/* Submit Button */}
        <button type="submit" disabled={!stripe || isLoading}>
          {isLoading ? 'Processing...' : 'Pay Now'}
        </button>
      </form>
    </div>
  );
};

export default CheckoutForm;
