import React, { useState, useEffect } from 'react';
import './AdminIntegrations.css';

const AdminIntegrations = () => {
  const [integrations, setIntegrations] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editIntegration, setEditIntegration] = useState(null);
  const [newIntegration, setNewIntegration] = useState({
    provider: '',
    api_key: '',
    status: 'Active',
  });

  // Fetch integration settings
  useEffect(() => {
    const fetchIntegrations = async () => {
      try {
        const response = await fetch('/admin/integrations', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (response.ok) {
          const data = await response.json();
          setIntegrations(data);
        } else {
          const errorData = await response.json();
          setError(errorData.error);
        }
      } catch (error) {
        setError('Failed to fetch integrations. Please try again later.');
      } finally {
        setIsLoading(false);
      }
    };

    fetchIntegrations();
  }, []);

  const handleEditClick = (integration) => {
    setEditIntegration(integration);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (editIntegration) {
      setEditIntegration({
        ...editIntegration,
        [name]: value,
      });
    } else {
      setNewIntegration({
        ...newIntegration,
        [name]: value,
      });
    }
  };

  const handleSaveClick = async () => {
    try {
      const response = await fetch(`/admin/integrations/${editIntegration.integration_id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          api_key: editIntegration.api_key,
          status: editIntegration.status,
        }),
      });

      if (response.ok) {
        // Update the list with the new changes
        setIntegrations((prevIntegrations) =>
          prevIntegrations.map((integration) =>
            integration.integration_id === editIntegration.integration_id
              ? editIntegration
              : integration
          )
        );
        setEditIntegration(null); // Exit edit mode
      } else {
        const errorData = await response.json();
        setError(errorData.error);
      }
    } catch (error) {
      setError('Failed to update integration. Please try again later.');
    }
  };

  const handleAddIntegration = async () => {
    try {
      const response = await fetch('/admin/integrations', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newIntegration),
      });

      if (response.ok) {
        const addedIntegration = await response.json();
        setIntegrations((prevIntegrations) => [...prevIntegrations, addedIntegration]);
        setNewIntegration({ provider: '', api_key: '', status: 'Active' });
      } else {
        const errorData = await response.json();
        setError(errorData.error);
      }
    } catch (error) {
      setError('Failed to add integration. Please try again later.');
    }
  };

  return (
    <div className="admin-integrations-page">
      <h1>Manage Integrations</h1>
      {isLoading ? (
        <p>Loading integrations...</p>
      ) : error ? (
        <p className="error-message">{error}</p>
      ) : (
        <>
          {integrations.length === 0 && <p>No integrations available.</p>}
          <div className="integration-cards">
            {integrations.map((integration) => (
              <div key={integration.integration_id} className="integration-card">
                {editIntegration && editIntegration.integration_id === integration.integration_id ? (
                  <>
                    <label>Provider:</label>
                    <input type="text" value={integration.provider} disabled />

                    <label>API Key:</label>
                    <input
                      type="text"
                      name="api_key"
                      value={editIntegration.api_key}
                      onChange={handleInputChange}
                    />

                    <label>Status:</label>
                    <select
                      name="status"
                      value={editIntegration.status}
                      onChange={handleInputChange}
                    >
                      <option value="Active">Active</option>
                      <option value="Inactive">Inactive</option>
                    </select>

                    <button onClick={handleSaveClick}>Save</button>
                  </>
                ) : (
                  <>
                    <p>
                      <strong>Provider:</strong> {integration.provider}
                    </p>
                    <p>
                      <strong>API Key:</strong> {integration.api_key}
                    </p>
                    <p>
                      <strong>Status:</strong> {integration.status}
                    </p>
                    <button onClick={() => handleEditClick(integration)}>Edit</button>
                  </>
                )}
              </div>
            ))}
          </div>

          {/* Add New Integration Form */}
          <div className="add-integration-form">
            <h2>Add New Integration</h2>
            <label>Provider:</label>
            <input
              type="text"
              name="provider"
              value={newIntegration.provider}
              onChange={handleInputChange}
              placeholder="e.g., Stripe, Mpesa"
            />

            <label>API Key:</label>
            <input
              type="text"
              name="api_key"
              value={newIntegration.api_key}
              onChange={handleInputChange}
              placeholder="Enter API Key"
            />

            <label>Status:</label>
            <select
              name="status"
              value={newIntegration.status}
              onChange={handleInputChange}
            >
              <option value="Active">Active</option>
              <option value="Inactive">Inactive</option>
            </select>

            <button onClick={handleAddIntegration}>Add Integration</button>
          </div>
        </>
      )}
    </div>
  );
};

export default AdminIntegrations;
