import React from 'react';

const Notifications = () => {
  // Hardcoded notification data for testing rendering
  const notifications = [
    { notification_id: 1, message: 'Your profile has been approved.', is_read: true, created_at: '2024-08-28T11:21:04' },
    { notification_id: 2, message: 'New event posted in your area.', is_read: false, created_at: '2024-09-10T09:15:30' },
    { notification_id: 3, message: 'Your ticket purchase was successful.', is_read: true, created_at: '2024-09-15T13:45:10' },
    { notification_id: 4, message: 'Profile update required.', is_read: false, created_at: '2024-09-18T16:30:00' },
    { notification_id: 5, message: 'Event reminder: Jazz concert tonight!', is_read: false, created_at: '2024-09-20T18:00:00' },
  ];

  return (
    <div className="notifications-container">
      <h2>Notifications</h2>

      <table className="notifications-table">
        <thead>
          <tr>
            <th>Message</th>
            <th>Status</th>
            <th>Created At</th>
          </tr>
        </thead>
        <tbody>
          {notifications.length > 0 ? (
            notifications.map((notification) => (
              <tr key={notification.notification_id}>
                <td>{notification.message}</td>
                <td>{notification.is_read}</td>
                <td>{new Date(notification.created_at).toLocaleString()}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="3">No notifications available.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Notifications;
