import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import { Elements } from '@stripe/react-stripe-js';
import CheckoutForm from './CheckoutForm';
import './LandingPage.css';

const stripePromise = loadStripe('pk_test_123pol');


const LandingPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const username = location.state?.username || 'Guest';

  const [countries, setCountries] = useState([]);
  const [locations, setLocations] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState('');
  const [selectedLocation, setSelectedLocation] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [events, setEvents] = useState([]);
  const [flippedEvents, setFlippedEvents] = useState({});
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [likedEvents, setLikedEvents] = useState({});

  // Fetch the list of countries from the server
  useEffect(() => {
    const fetchCountries = async () => {
      try {
        const response = await fetch('/events/countries');
        const data = await response.json();
        setCountries(data);
      } catch (error) {
        console.error('Error fetching countries:', error);
      }
    };

    fetchCountries();
  }, []);

  useEffect(() => {
    // Fetch the list of locations and categories based on selected country
    const fetchFilters = async () => {
      if (selectedCountry) {
        try {
          const response = await fetch(`/events/filters?country=${selectedCountry}`);
          const data = await response.json();
          setLocations(data.locations);
          setCategories(data.categories);
        } catch (error) {
          console.error('Error fetching filters:', error);
        }
      }
    };
  
    fetchFilters();
  }, [selectedCountry]);

  useEffect(() => {
    const fetchEvents = async () => {
      if (selectedCountry) {
        try {
          const url = `/events?country=${selectedCountry}&location=${selectedLocation}&category=${selectedCategory}&status=Approved`;
          const response = await fetch(url);
          const data = await response.json();

          // Get current date and filter events whose date is in the future
          const currentDate = new Date();
          const upcomingEvents = data.filter(event => new Date(event.event_date) > currentDate);

          setEvents(upcomingEvents);
        } catch (error) {
          console.error('Error fetching events:', error);
        }
      }
    };

    fetchEvents();
  }, [selectedCountry, selectedLocation, selectedCategory]);

  const handleEventClick = (e, event_id) => {
    if (e.target.classList.contains('booking-dropdown') || e.target.classList.contains('proceed-button') || e.target.classList.contains('like-button') || e.target.classList.contains('settings-dropdown')) {
      return;
    }

    setFlippedEvents((prev) => ({
      ...prev,
      [event_id]: !prev[event_id],
    }));
  };

  const handlePaymentMethodChange = (event, paymentMethod) => {
    setSelectedEvent(event);
    if (paymentMethod === 'stripe') {
      navigate(`/checkout`, { state: { event, method: 'stripe' } });
    } else if (paymentMethod === 'mpesa') {
      navigate(`/mpesa-payment`, { state: { event, method: 'mpesa' } });
    }
  };

  const handleCountryChange = (e) => {
    setSelectedCountry(e.target.value);
  };
  

  return (
    <div className="landing-page">
      <header className="header">
        <nav className="nav-links">
          <ul>
            <li><button onClick={() => navigate('/login')}>Logout</button></li>
            <li><button onClick={() => navigate('/settings')}>Settings</button></li>
            <li><button onClick={() => navigate('/notifications')}>Notifications</button></li>
            <li><button onClick={() => navigate('/my-events')}>My Events</button></li>
          </ul>
        </nav>
      </header>
      <div className="content">
        <h1>Welcome, {username}!</h1>
        <p>Select a country to view events:</p>
        <select onChange={handleCountryChange} value={selectedCountry}>
          <option value="">Select Country</option>
          {countries.map((country, index) => (
            <option key={index} value={country}>{country}</option>
          ))}
        </select>
  
        {events && events.length > 0 ? (
          <div className="events-container">
            {events.map((event) => (
              <div
                key={event.event_id}
                className={`event-card ${flippedEvents[event.event_id] ? 'flipped' : ''}`}
                onClick={(e) => handleEventClick(e, event.event_id)}
              >
                <div className="event-card-inner">
                  {/* Front of the card */}
                  <div className="event-card-front">
                    <img
                      src={`/event_images/${event.event_poster}`}
                      alt={event.event_name}
                      className="event-poster"
                    />
                    <div className="event-details">
                      <h3>{event.event_name}</h3>
                      <p><strong>Date:</strong> {new Date(event.event_date).toLocaleString()}</p>
                      <p><strong>Location:</strong> {event.event_location}</p>
                    </div>
                  </div>
  
                  {/* Back of the card */}
                  <div className="event-card-back">
                    <h3>{event.event_name}</h3>
                    <p><strong>Description:</strong> {event.event_description}</p>
                    <p><strong>Price:</strong> ${event.event_price}</p>
                    <p><strong>Tickets Remaining:</strong> {event.event_capacity} tickets</p>
  
                    {/* Book Tickets Dropdown */}
                    <select
                      className="booking-dropdown"
                      onClick={(e) => e.stopPropagation()}
                      onChange={(e) => handlePaymentMethodChange(event, e.target.value)}
                      defaultValue=""
                    >
                      <option value="" disabled>Buy Tickets</option>
                      <option value="stripe">Card Payment</option>
                      <option value="mpesa">M-Pesa</option>
                    </select>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          selectedCountry && <p>No approved events available for the selected filters.</p>
        )}
      </div>
    </div>
  );
}  
export default LandingPage;
